package stalbans.inertiagames;import java.awt.*;import java.awt.event.*;import javax.swing.*;public class Corner2GameEmbed extends SquareGameBoard {	JButton startButton;	public String getTitle() {		return "Corner 2";	}	public String getInstructionsLoc() {		return "text/corner2.html";	}	public RocketPanel newGamePanel() throws Exception {		return new CornerPanel(this);	}		public Rocket newStartingRocket() {		return new Rocket(20, 20, 2, 2, 5, 0, 5);	}		public void initGamePanelHook() {		((CornerPanel)gamePanel).setCorner(360, 210);	}	public void setupCardHook() {		startButton = new JButton();		startButton.setText("Start");		startButton.setDefaultCapable(false);		startButton.setSize(new java.awt.Dimension(100, 20));		startButton.setLocation(new java.awt.Point(430, 250));		startButton.setVisible(true);		startButton.setEnabled(true);		startButton.addActionListener(new ActionListener() {			public void actionPerformed(ActionEvent e) {				startButton.setVisible(false);				startButton.setEnabled(false);								kickButton.setEnabled(true);				rotateButton.setEnabled(true);				trackingButton.setEnabled(true);								gamePanel.start();			}		});				add(startButton);	}	public void replayHook() {		kickButton.setEnabled(false);		rotateButton.setEnabled(false);		trackingButton.setEnabled(false);				startButton.setEnabled(true);		startButton.setVisible(true);		repaint();	}		public boolean autoStartHook() {		return false;	}}