package stalbans.inertiagames;import java.awt.*;import java.awt.event.*;import javax.swing.*;public class RaceGameEmbed extends SquareGameBoard {		JLabel speedMeter;	JCheckBox autoKick;		public String getTitle() {		return "Race Game";	}		public String getInstructionsLoc() {		return "text/race.html";	}		public void setupCardHook()	{		speedMeter = new JLabel();		speedMeter.setFont(new java.awt.Font("SansSerif", 0, 12));		speedMeter.setSize(new java.awt.Dimension(100, 20));		speedMeter.setLocation(new java.awt.Point(320, 30));		speedMeter.setHorizontalAlignment(SwingConstants.RIGHT);				autoKick = new JCheckBox();		autoKick.setText("Auto Kick");		autoKick.setSize(new java.awt.Dimension(100, 20));		autoKick.setLocation(new java.awt.Point(430, 180));		autoKick.addActionListener(new ActionListener() {			public void actionPerformed(ActionEvent e) {				((RaceGamePanel)gamePanel).toggleAutokick();			}		});		this.add(speedMeter);		this.add(autoKick);	}		public RocketPanel newGamePanel() throws Exception {		return new RaceGamePanel(this);	}		public Rocket newStartingRocket() {		return new Rocket(gamePanel.getWidth()/2, 50, 0, 2);	}		public void initGamePanelHook() {		((RaceGamePanel)gamePanel).setSpeedometer(speedMeter);	}	public void replayHook() {		autoKick.setEnabled(true);		autoKick.setSelected(false);	}		public void gameCompletedHook() {		autoKick.setEnabled(false);	}}